mapper.select:
SELECT * FROM daily_monitoring


        resident_id=#{residentId}


        monitoring_date=#{monitoringDate}


ORDER BY monitoring_date DESC

mapper.update:
UPDATE daily_monitoring SET resident_id=#{residentId},
monitoring_date=#{monitoringDate},
blood_pressure=#{bloodPressure} ,
blood_sugar=#{bloodSugar},heart_rate=#{heartRate},temperature=#{temperature},notes=#{notes}
where id=#{id}
